require 'uri'
require 'sinatra'
require 'httparty'
require 'nokogiri'

get '/' do  # '/' -> simple routing
    #"Hello Sinatra~"
    send_file 'index.html'
end

# Variable Routing
get '/welcome/:name' do #params[:name] 에 String 형식으로 값이 저장되어 있음.
    " Welcome~~!! #{params[:name]} " #interpole nation
end

get '/cube/:number' do
    ans = params[:number].to_i ** 3
    
    "Ans : #{ans}"
end

get '/lunch' do
    lunch = ["멀티캠퍼스 20층", "김밥천국", "시골집", "순대국밥"]
    
    lunchimg ={
        "멀티캠퍼스 20층" => "data:image/jpeg;base64,",
        "김밥천국" => "data:image/jpeg;base64,",
        "시골집" => "data:image/jpeg;base64,",
        "순대국밥" => "data:image/jpeg;base64,"
    }        
    
    @menu = lunch.sample
    @menuimg = lunchimg[@menu]
    #send_file 'lunch.html' #html 로 할때
    erb :lunch  #ruby로 할때
end

get '/lottery' do
   @lotto = [*1..45].sample(6)
   #"오늘 로또 번호는 : #{lotto}"
   erb :lotto
end

get '/lol' do
    erb :lol
end


get '/lolsearch' do
    
    keyword = URI.encode(params[:userName]) # 날아온 Value URI.encode : 한글 및 빈칸 문제 해결.
    url = "http://www.op.gg/summoner/userName="   # www 가 없으면 잘 안됨.
    
    response = HTTParty.get(url+keyword)
    src = Nokogiri::HTML(response.body)
    
    @win = src.css('#SummonerLayoutContent > div.tabItem.Content.SummonerLayoutContent.summonerLayout-summary > div.SideContent > div.TierBox.Box > div.SummonerRatingMedium > div.TierRankInfo > div.TierInfo > span.WinLose > span.wins')
    @lose = src.css('#SummonerLayoutContent > div.tabItem.Content.SummonerLayoutContent.summonerLayout-summary > div.SideContent > div.TierBox.Box > div.SummonerRatingMedium > div.TierRankInfo > div.TierInfo > span.WinLose > span.losses')
    @name = params[:userName]
    
    erb :lolsearch
end
